        /// <summary>
        /// 批量获取股票行情数据
        /// </summary>
        /// <param name="request"></param>
        /// <param name="basedata"></param>
        /// <returns></returns>
        public List<ShareQuotesInfo> BatchGetShareQuotesInfo(BatchGetShareQuotesInfoRequest request, HeadBase basedata) 
        {
            if (request.Date == null)
            {
                request.Date = DateTime.Now.Date;
            }
            if (request.Date.Value.Date == DateTime.Now.Date)
            {
                request.Date = DateTime.Now.AddHours(-9).Date;
            }
            request.Date = DbHelper.GetLastTradeDate2(0, 0, 0, 0, request.Date);
            List<ShareQuotesInfo> result = new List<ShareQuotesInfo>();
            var shares_quotes_last = Singleton.Instance.sessionHandler.GetShares_Quotes_AppointDate_Session(request.Date.Value,false);
            var shares_base = Singleton.Instance.sessionHandler.GetShares_Base_Session();
            foreach (var shareKey in request.SharesKey)
            {
                if (!shares_base.ContainsKey(shareKey))
                {
                    continue;
                }
                if (!shares_quotes_last.ContainsKey(shareKey))
                {
                    continue;
                }
                var baseInfo = shares_base[shareKey];
                var quoteInfo = shares_quotes_last[shareKey];
                result.Add(new ShareQuotesInfo
                {
                    SharesCode = quoteInfo.SharesCode,
                    Market = quoteInfo.Market,
                    SharesName = baseInfo.SharesName,
                    ClosedPrice = quoteInfo.YestodayClosedPrice,
                    PresentPrice = quoteInfo.ClosedPrice,
                    OpenedPrice = quoteInfo.OpenedPrice,
                    TodayDealCount = quoteInfo.TotalCount,
                    TodayDealAmount = quoteInfo.TotalAmount,
                    CirculatingCapital = baseInfo.CirculatingCapital,
                    LimitUpTime = quoteInfo.PriceType == 1 && quoteInfo.LimitUpTime != null ? quoteInfo.LimitUpTime.Value : DateTime.Parse("9999-01-01 00:00:00"),
                    PriceType = quoteInfo.PriceType == 1 ? 1 : 0,
                });
            }
            return result.OrderByDescending(e => e.PriceType).ThenBy(e => e.LimitUpTime).ThenByDescending(e => e.RiseRate).ToList();
        }

        /// <summary>
        /// 获取单只股票基础信息
        /// </summary>
        /// <param name="request"></param>
        /// <param name="basedata"></param>
        /// <returns></returns>
        public ShareStatisticInfo GetShareStatisticInfo(GetShareQuotesInfoRequest request, HeadBase basedata) 
        {
            using (var db = new meal_ticketEntities())
            {
                var mySharesGroup = (from item in db.t_account_shares_group_rel
                                     where item.AccountId == basedata.AccountId && item.Market==request.Market && item.SharesCode==request.SharesCode
                                     select item).ToList();
                var conditionBuy = (from item in db.t_account_shares_conditiontrade_buy
                                    where item.AccountId == basedata.AccountId && item.Market == request.Market && item.SharesCode == request.SharesCode
                                    select item).FirstOrDefault();
                var groupRelList = (from item in db.t_account_shares_conditiontrade_buy_group_rel
                                    join item2 in db.t_account_shares_conditiontrade_buy_group on item.GroupId equals item2.Id
                                    where item2.AccountId == basedata.AccountId && item.Market == request.Market && item.SharesCode == request.SharesCode
                                    select item).ToList();


                
                var plateList = _getSharesPlateRelList(new List<SharesBaseInfo> 
                {
                    new SharesBaseInfo
                    {
                        Market=request.Market,
                        SharesCode=request.SharesCode
                    }
                }, basedata.AccountId, db);
                plateList = plateList.OrderByDescending(e => e.RiseRate).ToList();
                var shares_last_quotes_session = Singleton.Instance.sessionHandler.GetShares_Quotes_Last_Session();


                long key = long.Parse(request.SharesCode) * 10 + request.Market;
                long StockRate_All = 0;
                long StockRate_Now = 0;
                if (shares_last_quotes_session.ContainsKey(key))
                {
                    StockRate_All = shares_last_quotes_session[key].RateExpect;
                    StockRate_Now = shares_last_quotes_session[key].RateNow;
                }
                return new ShareStatisticInfo 
                {
                    SharesCode=request.SharesCode,
                    Market=request.Market,
                    ConditionId= conditionBuy == null ?0: conditionBuy.Id,
                    ConditionStatus = conditionBuy==null?1: conditionBuy.Status==1?3:2,
                    MySharesGroupList= mySharesGroup.Select(e=>e.GroupId).ToList(),
                    GroupList= groupRelList.Select(e=>e.GroupId).ToList(),
                    PlateList= plateList,
                    StockRate_Now= StockRate_Now,
                    StockRate_All= StockRate_All
                };
            }
        }

