                        db.SaveChanges();
                        buySetting.Add(temp);
                    }
                }

                var result = (from item in buySetting
                              where IsMain==true || item.Type==1 || item.Type == 2
                              orderby item.Type
                              select new AccountBuySettingInfo
                              {
                                  CreateTime = item.LastModified,
                                  Description = item.Description,
                                  Id = item.Id,
                                  Name = item.Name,
                                  ParValueJson = item.ParValueJson,
                                  Type = item.Type
                              }).ToList();
                return result;
            }
        }

