                        }
                        if (shareInfo.Plate_Tag.Tag_5Days == null)
                        {
                            shareInfo.Plate_Tag.Tag_5Days = new Dictionary<long, Statistic_Plate_Tag>();
                        }
                        if (shareInfo.Plate_Tag.Tag_10Days == null)
