                        tempInfo.ConditionStatus = groupRel.Select(e => e.x.Status).FirstOrDefault();
                        if (tempInfo.ConditionStatus == 0)
                        {
                            tempInfo.ConditionStatus = 1;
                        }
                        else if (tempInfo.ConditionStatus == 1)
