        private string ParseTag(int RiseLimitDays, int RiseLimitCount)
        {
            if (RiseLimitCount == 0)
            {
                return "";
            }
            if (RiseLimitDays == RiseLimitCount)
            {
                if (RiseLimitCount == 1)
                {
                    return "1板";
                }
                return string.Format("{0}连板", RiseLimitCount);
            }
            return string.Format("{0}天{1}板", RiseLimitDays, RiseLimitCount);
        }

